mapper.select:

	SELECT * 
	  FROM (
	  		SELECT A.*, 
	  			   rownum AS rn
	  		  FROM
	  		  	  (
	  		  	  	SELECT * FROM pj_notice_tbl
					 WHERE show = 'y'
					 ORDER BY notice_num DESC 
				  ) A
    		)
  	WHERE rn >= #{start} 
  	  AND rn <= #{end}

mapper.update:
UPDATE pj_notice_tbl
   SET notice_title = #{notice_title}, notice_content = #{notice_content}
 WHERE notice_num = #{notice_num}

mapper.delete:
UPDATE pj_notice_tbl 	
   SET show = 'n'
 WHERE notice_num = #{notice_num}
